import { useContext } from "react";
import Modal from "../UI/Modal";
import CartContext from '../../store/cart-context';

import classes from "./Cart.module.css";




const Cart = props =>{
    

    const cartCtx = useContext(CartContext);
    const totalAmount = `$${cartCtx.totalAmount.toFixed(2)}`;
    const hasItems = cartCtx.length > 0;
    const carItems = <ul className = {classes['cart-items']}>{cartCtx.items.map((item) =><li>{item.name}-{item.amount}-{item.amount * item.price}</li>)}</ul>
   
return (
    
    <Modal    onClose = {props.onClose}>
        {carItems}
        <div className ={classes.total}>
            <span>Total Amount</span>
            <span >{totalAmount}</span>
        </div>
        <div className = {classes.actions}>
            <button className = {classes['button--alt']} onClick = {props.onClose}>close</button>
            {!hasItems && <button  className = {classes.button} onClick = {props.onConfirm}>Order</button> } 
        </div>
    </Modal>
);
    
};

export default Cart;
