import { useContext } from 'react';
import CartContext from '../../store/cart-context';
import Modal from "../UI/Modal";
import MealItem from '../Meals/MealItem';
import classes from "./Cart.module.css";



const cartCtx = <MealItem onAddToCart/>;
const Cart = props =>{
    const carItems = <ul className = {classes['cart-items']}>{[{key :'c1',id:'c1',name:'Vegan Sushi',amount:2,price:12.99},].map((item) =><li>{item.name}</li>)}</ul>
return (
    <Modal onClose = {props.onClose}>
        {carItems}
        <div className ={classes.total}>
            <span>Total Amount</span>
            <span>36.12</span>
        </div>
        <div className = {classes.actions}>
            <button className = {classes['button--alt']} onClick = {props.onClose}>close</button>
            <button className = {classes.button} onClick = {props.onConfirm}>Order</button>
        </div>
    </Modal>
);
    
};

export default Cart;
